function checkColControl(data)
{
  ret = data.substr(0,data.length-1);
  ret=ret.split("-");
  res="<div class='control_box'><span class='right'><i class='fa fa-times' aria-hidden='true'></i></span></div>";
  ret.forEach(function(element){
    res+="<div class='col-sm-"+element+"' ondrop='dropContent(event)' ondragover='allowDrop(event)'></div>"
  });
  return res;
}
/*drag & drop related functions*/
flag=false;
function allowDrop(ev) {
    ev.preventDefault();
}

function drag(ev) {
    ev.dataTransfer.effectAllowed='copy';
    ev.dataTransfer.setData("text", ev.target.id);
}

function drop(ev) {
    ev.preventDefault();
    if(!flag)
    {
      ev.dataTransfer.dropEffect = "copy";
      var data = ev.dataTransfer.getData("text");
      $(".droparea").append(checkColControl(data));
    }
}
function dropContent(ev) {
    ev.preventDefault();
    flag=true;
    ev.dataTransfer.dropEffect = "copy";
    var data = ev.dataTransfer.getData("text");
    $(ev.target).append("<p>Hello</p>");

}
