function allowDrop(ev) {
    ev.preventDefault();
}

function drag(ev) {
    ev.dataTransfer.effectAllowed='copy';
    ev.dataTransfer.setData("text", ev.target.id);
}

function drop(ev) {
    ev.preventDefault();
    ev.dataTransfer.dropEffect = "copy";
    var data = ev.dataTransfer.getData("text");
    $(".droparea").append(data);

}
